import "./Home.css";
import Header from "../Component/Header";
import { useState } from "react";
import { Delete ,callApi } from "../services/callApi";

const Home = () => {
    const [text, setText] = useState("");
    const [users, setUsers] = useState([])
    const [list, setList] = useState();
    console.log("img",users)

    const  search=()=>{
     callApi(text).then(res => setUsers(res.data.items)).catch(console.log("FETCH ERROR"))
    }
    const DeleteItem=(id)=>{
        const newList =list.filter((item) => item.id !== id); 
        setList(newList);
       console.log("second");
    }

    return (
        <>
            <Header/>
                <div className="submitbutton">
                    <input className='search-input' value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder='Search Users...'></input>

                    <button className='submit-button' 
                    onClick={search}
                     >Submit</button>

                     <button className="clearbutton" 
                     onClick={()=>{DeleteItem(item.id)}} 
                     >Clear</button>
                </div>

                < div className="main">
                    {users.map((item,index)=>{
                        return(
                            <div key={index} className="card">
                                <div className="bodycard">
                                    <img alt="profile"src={item.avatar_url}></img>
                                    <div className="login"key={item.id}>{item.login}</div>
                                    <button className="More">More</button>
                                </div>
                            </div>) 
                    })}

                </div>
        </>
    );
};

export default Home;
