import "./Home.css";
import Header from "../Component/Header";
import { useState } from "react";
import {callApi} from "../services/callApi";
import Loading from "../Component/loading";

const Home = () => {

    const [text, setText] = useState("");
    console.log(text,"text")
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");

async function getUser  (params)  {
    const response = await fetch("https://api.github.com/search/users?q="+params)
    const data = await response.json()
    console.log(data)
}    

React.useEffect(() => {
getUser();
},[])
    const  search=()=>{
     getUser(text)
     .then(res => {
         setIsLoading(true);
         setUsers(res.data.items);
         })
     .catch(()=>{
         setErrorMessage("Unable to fetch user list");
        //  setIsLoading(false);
    }
         )
    }
    const DeleteItem=()=>{
        setText("")
        setUsers([])
    }


    return (
        <>
            <Header/>
                <div className="submitbutton">
                    <input className='search-input' value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder='Search Users...'></input>

                    <button className='submit-button' 
                    onClick={search}
                     >Submit</button>

                     <button className="clearbutton" 
                     onClick={DeleteItem} 
                     >Clear</button>



                </div>
                < div className="main">
                 {isLoading ? <Loading/> : 
                    users.map((item,index)=>{
                        return(
                            <div key={index} className="card">
                                <div className="bodycard">
                                    <img alt="profile"src={item.avatar_url}>{item.login}</img>
                                    <div className="login"key={item.id}></div>
                                    <button className="More">More</button>
                                </div>
                            </div>) 
                    })}

                    <div className="error">{errorMessage}</div>
               </div>
        </>
    );
};

export default Home;
