import "./Home.css";
import Header from "../Component/Header";
import { useState } from "react";
import {callApi} from "../services/callApi";
import Loading from "../Component/loading";

const Home = () => {
    const [text, setText] = useState("");
    const [users, setUsers] = useState([]);
    const [isLoading, setIsLoading] = useState(false);

    console.log("img",users)

    const  search=()=>{
     callApi(text).then(res => {setUsers(res.data.items).setIsLoading(true)}).catch(console.log("FETCH ERROR").setIsLoading(false))
    }
    const DeleteItem=()=>{
        setText("")
        setUsers([])
    }


    return (
        <>
            <Header/>
                <div className="submitbutton">
                    <input className='search-input' value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder='Search Users...'></input>

                    <button className='submit-button' 
                    onClick={search}
                     >Submit</button>

                     <button className="clearbutton" 
                     onClick={DeleteItem} 
                     >Clear</button>



                </div>
                < div className="main">
                    {users.map((item,index)=>{
                        return(
                            <div key={index} className="card">
                                <div className="bodycard">
                                    <img alt="profile"src={item.avatar_url}></img>
                                    <div className="login"key={item.id}>{isLoading ? <Loading/> : item.login}</div>
                                    <button className="More">More</button>
                                </div>
                            </div>) 
                    })}
               </div>
        </>
    );
};

export default Home;
